mapper.select:
select sssf.*,sss.SCENIC_SPOT_NAME ,sssi.COMPANY_NAME,sssi.SUBJECT_NAME
from SYS_SCENIC_SPOT_FENFUN sssf
left join SYS_SCENIC_SPOT sss on sssf.SCENIC_SPOT_ID = sss.SCENIC_SPOT_ID
left join SYS_SCENIC_SUBSCRIPTION_INFORMATION sssi on sssf.COMPANY_ID = sssi.COMPANY_ID


        and sssf.SUBJECT_ID = #{subjectId}


        and sssf.SCENIC_SPOT_ID = #{spotId}


        and  STR_TO_DATE(sssf.PROFIT_SHARING_MONTH,'%Y-%m') BETWEEN STR_TO_DATE(#{startDate},'%Y-%m') and STR_TO_DATE(#{endDate}, '%Y-%m')


        and sssf.FENRUN_SETTLEMEN_TYPE =#{type}


        and  sssf.COMPANY_ID = #{companyId}



group by FENRUN_SCENIC_SPOT_ID

select ROUND(SUM(wx),2) as wechatPaymentFlowTotal ,ROUND(SUM(svpf),2) as storedValuePaymentFlowTotal ,ROUND(SUM(wspf),2) as wechatStoredPaymentFlowTotal ,ROUND(SUM(ddpf),2) as depositDeductionPaymentFlowTotal ,ROUND(SUM(ta),2) as totalAmountTotal , ROUND(SUM(pfb),2) as platformFenrunBaseTotal, ROUND(SUM(pfe),2) as platformFenrunExpenditureTotal , ROUND(SUM(pfi),2) as platformFenrunIncomeTotal ,ROUND(SUM(pspm),2) as platformStatisticsPartsMaintenanceTotal ,ROUND(SUM(psse),2) as platformStatisticsSettlementExpenditureTotal , ROUND(SUM(pssi),2) as platformStatisticsSettlementIncomeTotal ,ROUND(SUM(afb),2) as actualFenrunBaseTotal ,ROUND(SUM(atfe),2) as actualTaxFenrunExpenditureTotal ,ROUND(SUM(atfi),2) as actualTaxFenrunIncomeTotal ,ROUND(SUM(spm),2) as spotPartsMaintenanceTotal ,ROUND(SUM(ase),2) as actualSettlemenExpenditureTotal , ROUND(SUM(asi),2) as actualSettlemenIncome from ( select WECHAT_PAYMENT_FLOW as wx, STORED_VALUE_PAYMENT_FLOW as svpf, WECHAT_STORED_PAYMENT_FLOW as wspf , DEPOSIT_DEDUCTION_PAYMENT_FLOW ddpf , TOTAL_AMOUNT as ta , PLATFORM_FENRUN_BASE as pfb , PLATFORM_FENRUN_EXPENDITURE as pfe ,PLATFORM_FENRUN_INCOME as pfi , PLATFORM_STATISTICS_PARTS_MAINTENANCE as pspm , PLATFORM_STATISTICS_SETTLEMENT_EXPENDITURE as psse, PLATFORM_STATISTICS_SETTLEMENT_INCOME pssi, ACTUAL_FENRUN_BASE as afb , ACTUAL_TAX_FENRUN_EXPENDITURE as atfe ,ACTUAL_TAX_FENRUN_INCOME atfi , SPOT_PARTS_MAINTENANCE as spm , ACTUAL_SETTLEMEN_EXPENDITURE as ase, ACTUAL_SETTLEMEN_INCOME as asi from SYS_SCENIC_SPOT_FENFUN
and SUBJECT_ID = #{subjectId}
and SCENIC_SPOT_ID = #{spotId}
and STR_TO_DATE(PROFIT_SHARING_MONTH,'%Y-%m') BETWEEN STR_TO_DATE(#{startDate},'%Y-%m') and STR_TO_DATE(#{endDate}, '%Y-%m')
and FENRUN_SETTLEMEN_TYPE =#{type}
GROUP BY SCENIC_SPOT_ID ORDER BY ta ) as p

select * from SYS_SCENIC_SPOT_FENFUN
where COMPANY_ID = #{companyId}
and SCENIC_SPOT_ID = #{spotId}
and PROFIT_SHARING_MONTH = #{month}
