mapper.select:
SELECT A.ROBOT_CODE , A.ROBOT_ID, B.SCENIC_SPOT_NAME , A.CLIENT_VERSION , A.ROBOT_CODE_SIM, A.ROBOT_MODEL FROM SYS_ROBOT A LEFT JOIN SYS_USERS_ROLE_SPOT D ON D.SCENIC_SPOT_ID = A.SCENIC_SPOT_ID LEFT JOIN SYS_SCENIC_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID
AND D.USER_ID = #{userId}
AND A.ROBOT_CODE LIKE CONCAT('%',#{robotCode},'%')
AND A.SCENIC_SPOT_ID = #{scenicSpotId}
AND A.ROBOT_CODE_SIM LIKE CONCAT('%',#{robotCodeSim},'%')

SELECT A.ROBOT_CODE AS robotCode, A.ROBOT_MODEL, A.ROBOT_ID, A.ROBOT_CODE, B.SCENIC_SPOT_NAME AS scenicSpotName, A.CLIENT_VERSION AS clientVersion, A.ROBOT_CODE_SIM AS robotCodeSim, C.ROBOT_BELARC_ADVISOR_ID, C.MASTER_CONTROL_MODEL, C.MASTER_CONTROL_FIRMWARE_VERSION, C.MASTER_CONTROL_ID, C.PAD_VERSION, C.PAD_FIRMWARE_VERSION, C.PAD_MANUFACTOR, C.MEID, C.DRIVE_MODEL, C.DRIVE_FIRMWARE_VERSION, C.DRIVER_MANUFACTURER, C.DRIVE_ID, C.MOTOR_MODEL, C.MOTOR_ID, C.MOTOR_MANUFACTURER, C.BATTERY_MODEL, C.BATTERY_MANUFACTURER, C.BATTERY_ID, C.ROBOT_POWER, C.CHARGER_MODEL, C.CHARGER_MANUFACTURER, C.CHARGER_ID, C.RANGING_MODULAR_MODEL, C.RANGING_MODULAR_VERSION, C.RANGING_MODULAR_MANUFACTURER, C.RANGING_MODULAR_ID, C.CREATE_DATE, C.UPDATE_DATE FROM SYS_ROBOT_BELARC_ADVISOR C LEFT JOIN SYS_ROBOT A ON A.ROBOT_ID = C.ROBOT_ID LEFT JOIN SYS_SCENIC_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID
1=1
AND A.ROBOT_CODE LIKE CONCAT('%',#{robotCode},'%')
AND A.ROBOT_CODE_SIM = #{robotCodeSim}
AND B.SCENIC_SPOT_ID = #{scenicSpotId}
ORDER BY A.ROBOT_CODE DESC
