mapper.insert:
insert into SYS_ROBOT_PARTS_MANAGEMENT (PARTS_MANAGEMENT_ID, ACCESSORIES_TYPE,
  FAULT_NAME, ACCESSORY_NAME, ACCESSORY_MODEL,
  ACCESSORY_PRICE, WARRANTY_PERIOD, APPLICABLE_MODELS,
  REMARKS, CREATE_TIME, UPDATE_TIME
  )
values (#{partsManagementId,jdbcType=BIGINT}, #{accessoriesType,jdbcType=VARCHAR},
  #{faultName,jdbcType=VARCHAR}, #{accessoryName,jdbcType=VARCHAR}, #{accessoryModel,jdbcType=VARCHAR},
  #{accessoryPrice,jdbcType=VARCHAR}, #{warrantyPeriod,jdbcType=VARCHAR}, #{applicableModels,jdbcType=VARCHAR},
  #{remarks,jdbcType=VARCHAR}, #{createTime,jdbcType=VARCHAR}, #{updateTime,jdbcType=VARCHAR}
  )

mapper.select:
SELECT sgs.AMOUNT, sgs.SPOT_ID, srpm.ACCESSORY_NAME FROM SYS_ROBOT_PARTS_MANAGEMENT srpm LEFT JOIN SYS_GOODS_STOCK sgs ON sgs.MANAGEMENT_ID = srpm.PARTS_MANAGEMENT_ID
1=1
AND sgs.SPOT_ID =#{spotId}
and ACCESSORIES_CODE =#{accessoriesCode}

SELECT

FROM SYS_ROBOT_PARTS_MANAGEMENT

    1=1

        and ACCESSORY_NAME = #{accessoryName}


        and ACCESSORIES_CODE = #{accessoriesCode}
