mapper.insert:
insert into SYS_SCENIC_SPOT_OPERATION_RULES (OPERATION_RULES_ID, SCENIC_SPOT_ID, OPERATE_PEOPLE,
                                             OPERATE_START_TIME, OPERATE_END_TIME, OPERATING_TIME,
                                             OPERATING_TYPE, CREATE_DATE, UPDATE_DATE
)
values (#{operationRulesId,jdbcType=BIGINT}, #{scenicSpotId,jdbcType=BIGINT}, #{operatePeople,jdbcType=VARCHAR},
        #{operateStartTime,jdbcType=VARCHAR}, #{operateEndTime,jdbcType=VARCHAR}, #{operatingTime,jdbcType=VARCHAR},
        #{operatingType,jdbcType=VARCHAR}, #{createDate,jdbcType=VARCHAR}, #{updateDate,jdbcType=VARCHAR}
       )

mapper.select:
select

from SYS_SCENIC_SPOT_OPERATION_RULES


      AND SCENIC_SPOT_ID = #{scenicSpotId}


      AND DATE_FORMAT(STR_TO_DATE(CREATE_DATE,'%Y-%m-%d' ),'%Y-%m')  between #{time} and #{time}
