mapper.update:
update SYS_SCENIC_SPOT_TARGET_AMOUNT_ASCRIPTION
  set TARGET_AMOUNT_ID = #{targetAmountId,jdbcType=BIGINT},
    UNDERTAKER_ID = #{undertakerId,jdbcType=VARCHAR},
    COMMITMENT_AMOUNT = #{commitmentAmount,jdbcType=VARCHAR},
    TYPE = #{TYPE,jdbcType=VARCHAR}
where TARGET_AMOUNT_ASCRIPTION_ID = #{targetAmountAscriptionId,jdbcType=BIGINT}

mapper.select:
select A.*, B.COMPANY_NAME as undertakerName, C.COMPANY_NAME as companyNameSubject from SYS_SCENIC_SPOT_TARGET_AMOUNT_ASCRIPTION A LEFT JOIN SYS_SCENIC_SUBSCRIPTION_INFORMATION B on A.UNDERTAKER_ID = B.COMPANY_ID LEFT JOIN SYS_SCENIC_SPOT_ASCRIPTION_COMPANY C on A.UNDERTAKER_ID = C.COMPANY_ID
AND A.TARGET_AMOUNT_ID = #{targetAmountId}
AND A.TYPE = #{type}
GROUP BY UNDERTAKER_ID
