mapper.select:
SELECT A.ID, A.SCENIC_SPOT_ID, A.ROBOT_TABLES, A.STOCK_NUMBER, A.TOTAL_PRICE, A.UNIT_PRICE, A.TYPE, A.BOND, A.STATE, A.START_TIME, A.END_TIME, A.CREATE_DATE, A.UPDATE_DATE, A.AGREEMENT, B.SCENIC_SPOT_NAME AS scenicSpotName FROM BUSINESS_AUCTION A LEFT JOIN SYS_SCENIC_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID
AND A.TYPE = #{type}
AND A.SCENIC_SPOT_ID = #{scenicSpotId}
ORDER BY A.CREATE_DATE DESC
