mapper.select:
SELECT
distinct DATE_FORMAT(A.CREATE_DATE,'%H:%i') AS timeName,
SUM(CAST(A.RECORD_ROBOT_ONLINE_ORDER as decimal(18,0))) AS RECORD_ROBOT_ONLINE_ORDER,
convert(SUM( CAST( A.RECORD_RECEPTION_DESK AS DECIMAL ( 18, 0 ) ) ) / #{days},decimal(10,2)) AS RECORD_RECEPTION_DESK
FROM
BUSINESS_CHART_DATA_RECORD A

  LEFT JOIN BUSINESS_USERS_SCENIC_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID

WHERE 1=1

  AND B.USER_ID = #{userId}

AND A.RECORD_TYPE = #{recordType}
AND A.SCENIC_SPOT_ID = #{scenicSpotId}
AND DATE( CREATE_DATE )  >=  #{stime}
AND DATE( CREATE_DATE )  <=  #{etime}
GROUP BY
timeName

SELECT
distinct DATE_FORMAT(A.CREATE_DATE,'%H:%i') AS timeName,
SUM(CAST(A.RECORD_ROBOT_ONLINE_ORDER as decimal(18,0))) AS RECORD_ROBOT_ONLINE_ORDER,
convert(SUM( CAST( A.RECORD_RECEPTION_DESK AS DECIMAL ( 18, 0 ) ) ) / #{days},decimal(10,2)) AS RECORD_RECEPTION_DESK
FROM
BUSINESS_CHART_DATA_RECORD_NEW A

  LEFT JOIN BUSINESS_USERS_SCENIC_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID

WHERE 1=1

  AND B.USER_ID = #{userId}

AND A.RECORD_TYPE = #{recordType}
AND A.SCENIC_SPOT_ID = #{scenicSpotId}
AND DATE( CREATE_DATE )  >=  #{stime}
AND DATE( CREATE_DATE )  <=  #{etime}
GROUP BY
timeName

    SELECT
    distinct DATE_FORMAT(A.CREATE_DATE,'%H:%i') AS timeName,
	SUM(CAST(A.RECORD_ROBOT_ONLINE_ORDER as decimal(18,0))) AS RECORD_ROBOT_ONLINE_ORDER,
	SUM(CAST(A.RECORD_RECEPTION_DESK as decimal(18,0))) AS RECORD_RECEPTION_DESK
    FROM
	BUSINESS_CHART_DATA_RECORD A

      LEFT JOIN BUSINESS_USERS_SCENIC_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID

    WHERE 1=1

      AND B.USER_ID = #{userId}

	AND A.RECORD_TYPE = #{recordType}
    AND DATE( CREATE_DATE )  >=  #{stime}
    AND DATE( CREATE_DATE )  <=  #{etime}
	GROUP BY timeName

