mapper.select:
SELECT A.ID, A.SCENIC_SPOT_ID, A.IMAGE_ID, A.SCENIC_SPOT_AREA, A.REVENUE_YEAR, A.REWARD_RATE, A.REAL_DATA, A.CREATE_TIME, A.UPDATE_TIME, A.SCENIC_SPOT_INTRODUCE, B.SCENIC_SPOT_NAME AS scenicSpotName, C.PROVINCE_ID AS provinceId, C.CITY_ID AS cityId, C.REGION_ID AS regionId FROM BUSINESS_SCENIC_SPOT_EXPAND A LEFT JOIN SYS_SCENIC_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID LEFT JOIN BUSINESS_SCENIC_SPOT_AREA C ON A.SCENIC_SPOT_ID = C.SCENIC_SPOT_ID
AND A.SCENIC_SPOT_ID = #{scenicSpotId}
ORDER BY A.CREATE_TIME DESC

SELECT A.ID, A.SCENIC_SPOT_ID, A.IMAGE_ID, A.SCENIC_SPOT_AREA, A.REVENUE_YEAR, A.REWARD_RATE, A.REAL_DATA, A.CREATE_TIME, A.UPDATE_TIME, A.SCENIC_SPOT_INTRODUCE, B.SCENIC_SPOT_NAME AS scenicSpotName, C.PROVINCE_ID AS provinceId, C.CITY_ID AS cityId, C.REGION_ID AS regionId FROM BUSINESS_SCENIC_SPOT_EXPAND A LEFT JOIN SYS_SCENIC_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID LEFT JOIN BUSINESS_SCENIC_SPOT_AREA C ON A.SCENIC_SPOT_ID = C.SCENIC_SPOT_ID LEFT JOIN BUSINESS_USERS_SCENIC_SPOT D ON A.SCENIC_SPOT_ID = D.SCENIC_SPOT_ID
AND D.USER_ID = #{userId}
ORDER BY A.CREATE_TIME DESC
