mapper.select:
select

from BUSINESS_USERS_SCENIC_SPOT
where USER_ID = #{userId}
AND SCENIC_SPOT_ID = #{scenicSpotId}

SELECT t.CREATE_TIME, t.USER_NAME AS userName, t.SCENIC_SPOT_NAME AS scenicSpotName, t.totalAmount AS totalAmount, t.amount AS amount, t.createTime AS createDate, t.ID, t.USER_ID, t.SCENIC_SPOT_ID, t.CONTRACT_ID, t.DIVIDEND_RATIO, t.REAL_TYPE, t.SWITCH_TYPE, t.TOTAL_NUM, t.COOPERATION_TYPE, t.CONTRACT_START_TIME, t.CONTRACT_END_TIME, t.CREATE_TIME, t.UPDATE_TIME FROM (SELECT A.ID, A.USER_ID, A.SCENIC_SPOT_ID, A.CONTRACT_ID, A.DIVIDEND_RATIO, A.REAL_TYPE, A.SWITCH_TYPE, A.TOTAL_NUM, A.COOPERATION_TYPE, A.CONTRACT_START_TIME, A.CONTRACT_END_TIME, A.CREATE_TIME, A.UPDATE_TIME, B.USER_NAME, C.SCENIC_SPOT_NAME, (SELECT SUM(AMOUNT) FROM BUSINESS_USERS_REVENUE_LOGS WHERE OBJECT_ID = A.SCENIC_SPOT_ID AND USER_ID = A.USER_ID AND REAL_TYPE = 1) AS totalAmount, (SELECT AMOUNT FROM BUSINESS_USERS_REVENUE_LOGS WHERE OBJECT_ID = A.SCENIC_SPOT_ID AND USER_ID = A.USER_ID AND REAL_TYPE = 1 ORDER BY CREATE_TIME DESC LIMIT 1) AS amount, (SELECT CREATE_TIME FROM BUSINESS_USERS_REVENUE_LOGS WHERE OBJECT_ID = A.SCENIC_SPOT_ID AND USER_ID = A.USER_ID AND REAL_TYPE = 1 ORDER BY CREATE_TIME DESC LIMIT 1) AS createTime FROM BUSINESS_USERS_SCENIC_SPOT A LEFT JOIN BUSINESS_USERS B ON A.USER_ID = B.ID LEFT JOIN SYS_SCENIC_SPOT C ON A.SCENIC_SPOT_ID = C.SCENIC_SPOT_ID
1=1
and A.USER_ID = #{userId}
and C.SCENIC_SPOT_NAME like CONCAT('%',#{scenicSpotName},'%')
) AS t ORDER BY t.CREATE_TIME DESC
