mapper.insert:
insert into SYS_ORDER_LOG (ORDER_LOG_ID, ORDER_LOG_LOGINNAME, ORDER_LOG_USERNAME, 
  ORDER_LOG_PHONE, ORDER_LOG_CLIENT, ORDER_LOG_NUMBER, 
  DEPOSIT_MONEY, SCENIC_SPOT_NAME, ORDER_LOG_REASON, 
  RETURN_RESULT_CODE, CREATE_DATE, UPDATE_DATE
  )
values (#{orderLogId,jdbcType=BIGINT}, #{orderLogLoginname,jdbcType=VARCHAR}, #{orderLogUsername,jdbcType=VARCHAR}, 
  #{orderLogPhone,jdbcType=VARCHAR}, #{orderLogClient,jdbcType=VARCHAR}, #{orderLogNumber,jdbcType=VARCHAR}, 
  #{depositMoney,jdbcType=VARCHAR}, #{scenicSpotName,jdbcType=VARCHAR}, #{orderLogReason,jdbcType=VARCHAR}, 
  #{returnResultCode,jdbcType=VARCHAR}, #{createDate,jdbcType=VARCHAR}, #{updateDate,jdbcType=VARCHAR}
  )

mapper.select:
SELECT A.*, B.ORDER_ROBOT_CODE AS orderRobotCode, B.REASONS_REFUNDS AS reasonsRefunds FROM SYS_ORDER_LOG A LEFT JOIN SYS_ORDER B ON A.ORDER_LOG_NUMBER = B.ORDER_NUMBER
AND A.ORDER_LOG_PHONE LIKE CONCAT('%',#{orderLogPhone},'%')
AND DATE(A.CREATE_DATE) >= #{time}
AND DATE(A.CREATE_DATE) >= #{startTime}
AND DATE(A.CREATE_DATE) <= #{endTime}
ORDER BY A.CREATE_DATE DESC
