mapper.insert:
insert into SYS_ORDER_OPERATION_LOG (OPERATION_ID, OPERATION_NAME, OPERATION_NUMBER, 
  OPERATION_PHONE, OPERATION_SCENIC_SPOT_NAME, 
  OPERATION_ROBOT_CODE, OPERATION_TOTAL_TIME, 
  OPERATION_FRONT, OPERATION_AFTER, CREATE_DATE
  )
values (#{operationId,jdbcType=BIGINT}, #{operationName,jdbcType=VARCHAR}, #{operationNumber,jdbcType=VARCHAR}, 
  #{operationPhone,jdbcType=VARCHAR}, #{operationScenicSpotName,jdbcType=VARCHAR}, 
  #{operationRobotCode,jdbcType=VARCHAR}, #{operationTotalTime,jdbcType=VARCHAR}, 
  #{operationFront,jdbcType=VARCHAR}, #{operationAfter,jdbcType=VARCHAR}, #{createDate,jdbcType=VARCHAR}
  )

mapper.select:
select

from SYS_ORDER_OPERATION_LOG


    AND OPERATION_PHONE LIKE CONCAT('%',#{operationPhone},'%')


    AND DATE(CREATE_DATE)  >=  #{time}


    AND DATE(CREATE_DATE)  >=  #{startTime}


    AND DATE(CREATE_DATE)  <=  #{endTime}


ORDER BY CREATE_DATE DESC

