mapper.insert:
insert into SYS_ROBOT_ADVERTISING (ADVERTISING_ID, ADVERTISING_SCENIC_SPOT_ID, 
  ADVERTISING_URL, ADVERTISING_VALID, ADVERTISING_NAME, 
  ADVERTISING_ORDER, CREATE_TIME, UPDATE_TIME
  )
values (#{advertisingId,jdbcType=BIGINT}, #{advertisingScenicSpotId,jdbcType=VARCHAR}, 
  #{advertisingUrl,jdbcType=VARCHAR}, #{advertisingValid,jdbcType=VARCHAR}, #{advertisingName,jdbcType=VARCHAR}, 
  #{advertisingOrder,jdbcType=VARCHAR}, #{createTime,jdbcType=VARCHAR}, #{updateTime,jdbcType=VARCHAR}
  )

mapper.select:
SELECT B.SCENIC_SPOT_NAME AS scenicSpotName, A.* FROM SYS_ROBOT_ADVERTISING A LEFT JOIN SYS_SCENIC_SPOT B ON B.SCENIC_SPOT_ID = A.ADVERTISING_SCENIC_SPOT_ID
AND A.ADVERTISING_SCENIC_SPOT_ID = #{scenicSpotId}
ORDER BY A.CREATE_TIME DESC
