mapper.insert:
insert into SYS_ROBOT_APP_VERSION (VERSION_ID, VERSION_URL, VERSION_NUMBER, 
  VERSION_DESCRIPTION, SCENIC_SPOT_ID, CREATE_DATE
  )
values (#{versionId,jdbcType=BIGINT}, #{versionUrl,jdbcType=VARCHAR}, #{versionNumber,jdbcType=VARCHAR}, 
  #{versionDescription,jdbcType=VARCHAR}, #{scenicSpotId,jdbcType=BIGINT}, #{createDate,jdbcType=VARCHAR}
  )

mapper.update:
update SYS_SCENIC_SPOT
set AUTO_UPDATE_MONITOR =#{autoUpdateMonitor,jdbcType=VARCHAR}
where SCENIC_SPOT_ID =#{scenicSpotId,jdbcType=BIGINT}

mapper.select:
select A.*, B.SCENIC_SPOT_NAME AS scenicSpotName from SYS_ROBOT_APP_VERSION A LEFT JOIN SYS_SCENIC_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID
AND A.SCENIC_SPOT_ID = #{sysRobotAppVersion.scenicSpotId}
ORDER BY A.CREATE_DATE DESC

SELECT C.VERSION_ID, C.VERSION_URL, C.VERSION_NUMBER, C.VERSION_DESCRIPTION, B.SCENIC_SPOT_ID, C.CREATE_DATE, B.SCENIC_SPOT_NAME AS scenicSpotName, B.AUTO_UPDATE_MONITOR AS autoUpdateMonitor FROM SYS_USERS_ROLE_SPOT A LEFT JOIN SYS_SCENIC_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID LEFT JOIN SYS_ROBOT_APP_VERSION C ON B.SCENIC_SPOT_ID = C.SCENIC_SPOT_ID
AND A.USER_ID = #{userId}
AND B.SCENIC_SPOT_NAME LIKE CONCAT('%',#{scenicSpotName},'%')
AND C.VERSION_NUMBER LIKE CONCAT('%',#{versionNumber},'%')
AND B.AUTO_UPDATE_MONITOR =#{autoUpdateMonitor}
ORDER BY C.CREATE_DATE DESC
