mapper.update:
update SYS_SCENIC_SPOT_CERTIFICATE_SPOT
set SCENIC_SPOT_ID = #{scenicSpotId,jdbcType=BIGINT},
  CERTIFICATE_ID = #{certificateId,jdbcType=BIGINT},
  CREATE_DATE = #{createDate,jdbcType=VARCHAR},
  UPDATE_DATE = #{updateDate,jdbcType=VARCHAR}
where CERTIFICATE_SPOT_ID = #{certificateSpotId,jdbcType=BIGINT}

mapper.select:
SELECT C.SCENIC_SPOT_NAME AS scenicSpotName, D.MERCHANT_NAME AS merchantName, A.CREATE_DATE, A.SCENIC_SPOT_ID, A.CERTIFICATE_ID, A.CERTIFICATE_SPOT_ID, D.CERT_FILE_NAME, D.MERCHANT_SECRET, D.MERCHANT_NUMBER FROM SYS_SCENIC_SPOT_CERTIFICATE_SPOT A LEFT JOIN SYS_USERS_ROLE_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID LEFT JOIN SYS_SCENIC_SPOT C ON A.SCENIC_SPOT_ID = C.SCENIC_SPOT_ID LEFT JOIN WECHAT_BUSINESS_MANAGEMENT D ON A.CERTIFICATE_ID = D.MERCHANT_ID
AND B.USER_ID = #{userId}
AND A.SCENIC_SPOT_ID = #{scenicSpotId}
ORDER BY A.CREATE_DATE DESC
