mapper.update:
update SYS_SCENIC_SPOT_IMG
set SCENIC_SPOT_ID = #{scenicSpotId,jdbcType=BIGINT},
  SCNEIC_SPOT_IMG_URL = #{scneicSpotImgUrl,jdbcType=VARCHAR},
  CREATE_DATE = #{createDate,jdbcType=VARCHAR},
  UPDATE_DATE = #{updateDate,jdbcType=VARCHAR}
where SCNEIC_SPOT_IMG_ID = #{scneicSpotImgId,jdbcType=BIGINT}

mapper.select:
SELECT B.SCENIC_SPOT_ID, B.SCENIC_SPOT_NAME AS scenicSpotName, C.SCNEIC_SPOT_IMG_URL FROM SYS_USERS_ROLE_SPOT A LEFT JOIN SYS_SCENIC_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID LEFT JOIN SYS_SCENIC_SPOT_IMG C ON B.SCENIC_SPOT_ID = C.SCENIC_SPOT_ID WHERE A.USER_ID = #{userId}
AND B.SCENIC_SPOT_ID = #{scenicSpotId}
ORDER BY B.ROBOT_WAKEUP_WORDS,B.CREATE_DATE
