SELECT B.SCENIC_SPOT_ID, B.SCENIC_SPOT_NAME, B.SCENIC_SPOT_CONTACT, B.SCENIC_SPOT_PHONE, B.SCENIC_SPOT_EMAIL, B.SCENIC_SPOT_ADDRES, B.SCENIC_SPOT_POSTAL_CODE, B.SCENIC_SPOT_ROBOT_TOTAL, B.SCENIC_SPOT_STATUS, B.SCENIC_SPOT_OPENWORD, B.SCENIC_SPOT_CLOSEWORD, B.SCENIC_SPOT_BEYOND_PRICE, B.SCENIC_SPOT_WEEKEND_PRICE, B.SCENIC_SPOT_NORMAL_PRICE, B.SCENIC_SPOT_WEEKEND_RENT_PRICE, B.SCENIC_SPOT_NORMAL_RENT_PRICE, B.SCENIC_SPOT_WEEKEND_TIME, B.SCENIC_SPOT_NORMAL_TIME, B.SCENIC_SPOT_REMAINING_TIME, B.SCENIC_SPOT_RENT_TIME, B.COORDINATE_RANGE, B.SCENIC_SPOT_DEPOSIT, B.NORMAL_CAPPED_PRICE, B.WEEKEND_CAPPED_PRICE, B.RANDOM_BROADCAST_TIME, B.ROBOT_WAKEUP_WORDS, B.CREATE_DATE, B.UPDATE_DATE, B.SCENIC_SPOT_FID, B.SCENIC_SPOT_FREQUENCY, B.SCENIC_SPOT_FENCE_TIME, B.SCENIC_SPOT_FORBIDDEN_ZONE_TIME, B.TEST_START_TIME, B.TRIAL_OPERATIONS_TIME, B.FORMAL_OPERATION_TIME, B.STOP_OPERATION_TIME, B.WORK_TIME, B.CLOSING_TIME, B.WORK_BROADCAST, B.CLOSING_BROADCAST, B.HUNT_SWITCH, B.HORN_SWITCH, B.REPEAT_STATUS, B.SETTLEMENT_METHOD, B.HUNT_QUANTITY, B.FREE_TIME_SETTING, B.LAMP_OPENING_TIME, B.LAMP_CLOSING_TIME, B.HEAT, C.SCNEIC_SPOT_IMG_URL AS scenicSpotUrlName, D.SCENIC_SPOT_FNAME AS scenicSpotRegion, E.COMPANY_ID, E.COMPANY_NAME AS companyName, B.AUTO_UPDATE_MONITOR FROM SYS_USERS_ROLE_SPOT A LEFT JOIN SYS_SCENIC_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID LEFT JOIN SYS_SCENIC_SPOT_IMG C ON B.SCENIC_SPOT_ID = C.SCENIC_SPOT_ID LEFT JOIN SYS_SCENIC_SPOT_BINDING D ON B.SCENIC_SPOT_FID = D.SCENIC_SPOT_FID LEFT JOIN SYS_SCENIC_SPOT_ASCRIPTION_COMPANY E ON B.COMPANY_ID = E.COMPANY_ID
AND A.USER_ID = #{userId}
AND B.SCENIC_SPOT_NAME LIKE CONCAT('%',#{scenicSpotName},'%')
AND B.ROBOT_WAKEUP_WORDS = #{robotWakeupWords}
ORDER BY B.CREATE_DATE DESC

SELECT B.SCENIC_SPOT_ID, B.SCENIC_SPOT_NAME, B.SCENIC_SPOT_CONTACT, B.SCENIC_SPOT_PHONE, B.SCENIC_SPOT_EMAIL, B.SCENIC_SPOT_ADDRES, B.SCENIC_SPOT_POSTAL_CODE, B.SCENIC_SPOT_ROBOT_TOTAL, B.SCENIC_SPOT_STATUS, B.SCENIC_SPOT_OPENWORD, B.SCENIC_SPOT_CLOSEWORD, B.SCENIC_SPOT_BEYOND_PRICE, B.SCENIC_SPOT_WEEKEND_PRICE, B.SCENIC_SPOT_NORMAL_PRICE, B.SCENIC_SPOT_WEEKEND_RENT_PRICE, B.SCENIC_SPOT_NORMAL_RENT_PRICE, B.SETTLEMENT_METHOD, B.SCENIC_SPOT_WEEKEND_TIME, B.SCENIC_SPOT_NORMAL_TIME, B.SCENIC_SPOT_REMAINING_TIME, B.SCENIC_SPOT_RENT_TIME, B.COORDINATE_RANGE, B.SCENIC_SPOT_DEPOSIT, B.NORMAL_CAPPED_PRICE, B.WEEKEND_CAPPED_PRICE, B.RANDOM_BROADCAST_TIME, B.ROBOT_WAKEUP_WORDS, B.CREATE_DATE, B.UPDATE_DATE, B.SCENIC_SPOT_FID, B.SCENIC_SPOT_FREQUENCY, B.SCENIC_SPOT_FENCE_TIME, B.SCENIC_SPOT_FORBIDDEN_ZONE_TIME, B.TEST_START_TIME, B.TRIAL_OPERATIONS_TIME, B.FORMAL_OPERATION_TIME, B.STOP_OPERATION_TIME, B.WORK_TIME, B.CLOSING_TIME, B.WORK_BROADCAST, B.CLOSING_BROADCAST, B.HUNT_SWITCH, B.HORN_SWITCH, B.REPEAT_STATUS, B.HUNT_QUANTITY, B.FREE_TIME_SETTING, B.LAMP_OPENING_TIME, B.LAMP_CLOSING_TIME, B.HEAT, B.HOLIDAY_STRING, B.SMALL_NETWORK, B.TREASURE_COOLDOWN_TIME, B.COOLDOWN_TIME, C.SCNEIC_SPOT_IMG_URL AS scenicSpotUrlName, D.SCENIC_SPOT_FNAME AS scenicSpotRegion, E.COMPANY_ID, E.COMPANY_NAME AS companyName, F.IMAGE_ID, F.SCENIC_SPOT_AREA, F.SCENIC_SPOT_INTRODUCE, F.REVENUE_YEAR, F.REWARD_RATE, F.REAL_DATA as realData, G.PROVINCE_ID AS provinceId, G.CITY_ID AS cityId, G.REGION_ID AS regionId, B.AUTO_UPDATE_MONITOR
FROM SYS_USERS_ROLE_SPOT A LEFT JOIN SYS_SCENIC_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID LEFT JOIN SYS_SCENIC_SPOT_IMG C ON B.SCENIC_SPOT_ID = C.SCENIC_SPOT_ID LEFT JOIN SYS_SCENIC_SPOT_BINDING D ON B.SCENIC_SPOT_FID = D.SCENIC_SPOT_FID LEFT JOIN SYS_SCENIC_SPOT_ASCRIPTION_COMPANY E ON B.COMPANY_ID = E.COMPANY_ID LEFT JOIN BUSINESS_SCENIC_SPOT_EXPAND F ON B.SCENIC_SPOT_ID = F.SCENIC_SPOT_ID LEFT JOIN BUSINESS_SCENIC_SPOT_AREA G ON A.SCENIC_SPOT_ID = G.SCENIC_SPOT_ID
AND A.USER_ID = #{userId}
AND B.SCENIC_SPOT_NAME LIKE CONCAT('%',#{scenicSpotName},'%')
AND B.ROBOT_WAKEUP_WORDS = #{robotWakeupWords}
ORDER BY B.CREATE_DATE DESC

SELECT B.SCENIC_SPOT_NAME AS scenicSpotName, B.SCENIC_SPOT_BEYOND_PRICE AS scenicSpotBeyondPrice, B.SCENIC_SPOT_WEEKEND_PRICE AS scenicSpotWeekendPrice, B.SCENIC_SPOT_NORMAL_PRICE AS scenicSpotNormalPrice, B.SCENIC_SPOT_WEEKEND_RENT_PRICE AS scenicSpotWeekendRentPrice, B.SCENIC_SPOT_NORMAL_RENT_PRICE AS scenicSpotNormalRentPrice, B.SCENIC_SPOT_WEEKEND_TIME AS scenicSpotWeekendTime, B.SCENIC_SPOT_NORMAL_TIME AS scenicSpotNormalTime, B.SCENIC_SPOT_RENT_TIME AS scenicSpotRentTime, B.SCENIC_SPOT_DEPOSIT AS scenicSpotDeposit, B.RANDOM_BROADCAST_TIME AS randomBroadcastTime, B.ROBOT_WAKEUP_WORDS AS robotWakeupWords, B.CREATE_DATE AS createDate, B.SETTLEMENT_METHOD FROM SYS_USERS_ROLE_SPOT A LEFT JOIN SYS_SCENIC_SPOT B ON A.SCENIC_SPOT_ID = B.SCENIC_SPOT_ID
AND A.USER_ID = #{userId}
AND B.SCENIC_SPOT_NAME LIKE CONCAT('%',#{scenicSpotName},'%')
AND B.ROBOT_WAKEUP_WORDS = #{robotWakeupWords}
ORDER BY A.CREATE_DATE DESC

