mapper.select:
select

from SYS_SCENIC_SPOT_PRICE_MODIFICATION_LOG


    AND PRICE_MODIFICATION_SPOT_NAME LIKE CONCAT('%',#{priceModificationSpotName},'%')


    AND DATE(CREATE_DATE)  >=  #{time}


    AND DATE(CREATE_DATE)  >=  #{startTime}


    AND DATE(CREATE_DATE)  <=  #{endTime}


    AND TYPE = #{type}


ORDER BY CREATE_DATE DESC

	select

  from SYS_SCENIC_SPOT_PRICE_MODIFICATION_LOG


      AND PRICE_MODIFICATION_SPOT_NAME LIKE CONCAT('%',#{priceModificationSpotName},'%')


      AND DATE(CREATE_DATE)  >=  #{time}


