mapper.insert:
insert into SYS_SCENIC_SPOT_RESOURCE_VERSION (RES_ID, SCENIC_SPOT_ID, RES_VERSION, 
  RES_TYPE, CREATE_DATE, UPDATE_DATE
  )
values (#{resId,jdbcType=BIGINT}, #{scenicSpotId,jdbcType=BIGINT}, #{resVersion,jdbcType=VARCHAR}, 
  #{resType,jdbcType=VARCHAR}, #{createDate,jdbcType=VARCHAR}, #{updateDate,jdbcType=VARCHAR}
  )

mapper.select:
SELECT

FROM
SYS_SCENIC_SPOT_RESOURCE_VERSION

    AND SCENIC_SPOT_ID = #{sysScenicSpotResourceVersion.scenicSpotId}
  AND RES_TYPE = #{sysScenicSpotResourceVersion.resType}

mapper.update:
update SYS_SCENIC_SPOT_RESOURCE_VERSION
set RES_VERSION = #{resVersion,jdbcType=VARCHAR},
  UPDATE_DATE = #{updateDate,jdbcType=VARCHAR}
where SCENIC_SPOT_ID = #{scenicSpotId,jdbcType=BIGINT}
AND RES_TYPE = #{resType,jdbcType=VARCHAR}
